function scrollTopButton(){var i=220,t=500,n=$('<div class="am-scroll-top"><\/div>');n.appendTo("body");$(window).on("scroll",function(){$(this).scrollTop()>i?n.fadeIn(t):n.fadeOut(t)});n.on("touchstart mouseup",function(n){$("html, body").animate({scrollTop:0},t);n.preventDefault()});$(".carousel").carousel({interval:2e3})}$(document).ready(function(){$(function(){$('[data-toggle="tooltip"]').tooltip()})});